// pega o campo que é uma string JSON
const raw = items[0]?.json?.concatenated_items;
if (!raw) {
  throw new Error('Não encontrei concatenated_items em items[0].json');
}

let invoices;
try {
  invoices = JSON.parse(raw);
} catch (e) {
  throw new Error('Falha ao dar parse em concatenated_items: ' + e.message);
}

// extrai os ids válidos
const ids = invoices
  .map(inv => inv.id)
  .filter(id => typeof id === 'string' && id.trim() !== '');

// devolve um item por id
return ids.map(id => ({ json: { id } }));

