  const vencUTC = Date.UTC(ano, mes - 1, dia);
  const hoje = new Date();
  const hojeUTC = Date.UTC(hoje.getUTCFullYear(), hoje.getUTCMonth(), hoje.getUTCDate());
  return Math.floor((vencUTC - hojeUTC) / (1000 * 60 * 60 * 24)); // inteiro, positivo = dias faltando
})() }}

